import React from "react";

class CounterRefactoring extends React.Component {
    constructor() {
      super();
      this.state = { counter: 0 };
      this.timer = null;
    }

    componentDidMount() {
        this.timer = setInterval(() => {
            this.setState({ counter: this.state.counter +1 });
        }, 1000);
    }

    componentWillUnmount() {
        clearInterval(this.timer);
    }

    render() {
      return <span> You spent: {this.state.counter} </span>
    }
}

const second = () => {
  return <span> You spent: {this.state.counter} </span>
}


export default CounterRefactoring;
