import React, {useState} from "react";
import Form from "./AppComponents/Form";
import Section from "./AppComponents/Section";
import List from "./AppComponents/List";

const appTitle = "To-Do App";
const list = [
    {id:1 ,title: "Test #1", completed: false},
    {id:2 ,title: "Test #2", completed: true},
    {id:3 ,title: "Test #3", completed: false},
    {id:4 ,title: "Test #4", completed: false},
    {id:5 ,title: "Test #5", completed: false}
];

const App = () => {
    const [todoList, setTodoList] = useState(list);
    const addTodo = (item) => {
        setTodoList((oldList) => [...oldList, item]);
    }


    return <div className="ui container center aligned">
            <Section>
                <h1>{appTitle}</h1>
            </Section>

            <Section>
                <Form addTodo={addTodo} />
            </Section>

            <Section>
                <List list={todoList} />
            </Section>
        </div>;
};

export default App;
