import React, {useState, useEffect} from "react";
import todos from "./apis";

import Form from "./AppComponents/Form";
import Section from "./AppComponents/Section";
import List from "./AppComponents/List";

const appTitle = "To-Do App";

const App = () => {
    const [todoList, setTodoList] = useState([]);

    useEffect(() => {
        async function fetchData() {
            const { data } = await todos.get("/todos");
            setTodoList(data);
        }
        fetchData();
    }, []);

    const addTodo = async(item) => {
        const { data } = await todos.post("/todos", item);
        setTodoList((oldList) => [...oldList, data]);
    };

    const removeTodo = async (id) => {
        const response = await todos.delete("/todos")
        setTodoList((oldList) => oldList.filter((item) => item.id !== id));
    }


    return <div className="ui container center aligned">
            <Section>
                <h1>{appTitle}</h1>
            </Section>

            <Section>
                <Form addTodo={addTodo} />
            </Section>

            <Section>
                <List 
                removeTodoListProp={removeTodo} 
                list={todoList} 
                />
            </Section>
        </div>;
};

export default App;
