import React, {useState} from "react";
import Form from "./AppComponents/Form";
import Section from "./AppComponents/Section";
import List from "./AppComponents/List";

const appTitle = "To-Do App";
const list = [
    {title: "Test #1", completed: false},
    {title: "Test #2", completed: true},
    {title: "Test #3", completed: false},
    {title: "Test #4", completed: false},
    {title: "Test #5", completed: false}
];

const App = () => {
    const [todoList, setTodoList] = useState(list)


    return <div className="ui container center aligned">
            <Section>
                <h1>{appTitle}</h1>
            </Section>

            <Section>
                <Form />
            </Section>

            <Section>
                <List list={todoList} />
            </Section>
        </div>;
};

export default App;
